{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"# %% [code]\nfrom flask import Flask,render_template\nimport pickle\n\napp = Flask(__name__)\nmodel = pickle.load(open('cat_model.pkl','rb'))\n\n@app.route('/')\ndef home():\n    return render_template('index.html')\n\n@app.route('/predict',methods=['POST'])\ndef predict():\n    Age = request.form.get('Age')\n    Sex = request.form.get('Sex')\n    ChestPainType = request.form.get('ChestPainType')\n    Cholesterol = request.form.get('Cholesterol')\n    FastingBS = request.form.get('FastingBS')\n    MaxHR = request.form.get('MaxHR')\n    ExerciseAngina = request.form.get('ExerciseAngina')\n    OldPeak = request.form.get('OldPeak')\n    ST_Slope = request.form.get('ST_Slope')\n    \n    result = model.predict([[Age,Sex,ChestPainType,Cholesterol,FastingBS,MaxHR,ExerciseAngina,OldPeak,ST_Slope]])\n    \n    if result == 0:\n        return \"You're safe, according to the data you don't have any heart disease. Stay Healthy\"\n    else:\n        return \"According to your data, you're suffering some kind of heart disease. Please consult youe doctor immediately\"\n    \n\nif __name__ = '__main__':\n    app.run(debug=True)\n    ","metadata":{"_uuid":"76717fd8-4f5f-4139-b2ac-995d507a8b33","_cell_guid":"1c2a620b-3503-445c-8478-5d323189bd6f","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}